import queryAsync from "../../mysql";
import crypto from 'crypto';
import jwt from 'jsonwebtoken';

async function auth(req, res) {
  let data = JSON.parse(req.body);
  if(data[0].action === "login") {
    // let name = data[1].name;
    let email = data[2].email;
    let pass = data[3].password;
    let hash = crypto.createHash('sha256').update(pass).digest('base64');
    
    let rows = await queryAsync(`
      SELECT * FROM register WHERE email LIKE "${email}"`
    );

    try{
      if(rows.length > 0 && rows[0].password == hash) {
        let token = jwt.sign({email: email, role: rows[0].role}, 'ShokoDidIt');
        res.setHeader('Set-Cookie', `logInCookie=${token}`);
        res.statusCode = 200; 
        res.end();
      }else{
        res.statusCode = 401;
        res.end();
      }
    } catch(err) {
      res.statusCode = 401;
      res.end();
    }
  } else if (data[0].action === "logout") {
    console.log("logout")
    res.setHeader('Set-Cookie', 'logInCookie=');
    res.statusCode = 200; 
    res.end();
  }
}


export default auth
