import NavBar from './NavBar';
import MetaHead from './Head';
import Footer from "./Footer";
import Link from 'next/link';

import '../style.css';
import { useState } from 'react';


function Index() {

  const [login, loginStatus] = useState([]);
  
  const [name, setName] = useState([""]);
  const [email, setEmail] = useState([""]);
  const [pass, setPass] = useState([""]);

  function handleChange(e){
    let target = e.target;
    if(target == name) {
      setName(prev => prev.concat(target.value));
      console.log("name" + name);
    }else if(target == email) {
      setEmail(prev => prev.concat(target.value));
      console.log(email);
    }else{
      setPass(prev => prev.concat(target.value));
      console.log(pass);
    }
  }

  let data = {
    name: {name},
    email: {email},
    password: {}
  }


  function handleSubmit (e) {
    e.preventDefault();
    
    fetch('http://localhost:3000/api/auth', {
      method: 'POST',
      body: JSON.stringify(data),
    })
    // .then(res => res.json())
    // .then(json => {
    //   console.log(json);
    // })
  }

  return(
    <>
      <MetaHead />
      <div className="App">
        <header className='elseHeader'>
          <div>
            <Link href="/">
              <a>
                <img 
                  src="../static/estate-logo.svg" 
                  width="300" height="100"
                  alt="Ultimo Real Estate, Sydney, Australia" 
                />
              </a>
            </Link>
          </div>
          <NavBar />
        </header>

        <main id="loginMain">
          <h1>Log In</h1>
          <div className="display">
            <div> </div>
            <div className="login">
              <form 
                action="/login" 
                method="POST"
                onSubmit ={handleSubmit}
              >
                <div className="loginForm">
                  <label htmlFor="name">Name</label>
                  <br />
                  <input 
                    type="text" 
                    id="name" 
                    name="name" 
                    placeholder="your full name" 
                    onChange = {handleChange}
                    required 
                  />
                </div>
                <div className="loginForm">
                  <label htmlFor="email">Email address</label>
                  <br />
                  <input 
                    type="email" 
                    id="email" 
                    name="email" 
                    placeholder="you@example.com" 
                    onChange = {handleChange}
                    required 
                  />
                </div>
                <div className="loginForm">
                  <label htmlFor="password">Password</label>
                  <br />
                  <input 
                    type="password" 
                    id="password" 
                    name="password" 
                    placeholder="Enter 6 characters or more" 
                    onChange = {handleChange}
                    required 
                  />
                </div>
                <button 
                  type="submit"
                  id = "submitBtn"
                >
                  Log In
                </button>
                <p style={{ marginTop: '20px' }}>
                  Not a member yet?.....
                  <Link href="/register" >
                    <a>Register</a>
                  </Link>
                </p>
              </form>
              <form action="/logout" method="POST">
                <button type="submit" id="logout">Log Out</button>
              </form>
            </div>
          </div>

        </main>

        <Footer />

      </div>

    </>
  );
}

export default Index;
