import NavBar from './NavBar';
import MetaHead from './Head';
import Footer from "./Footer";
import Link from 'next/link';
import queryAsync from '../mysql';

import '../style.css';

export async function getServerSideProps(req, res){

  let idNumber = parseInt(req.query.id);
  console.log('Hey' + idNumber);
  let queryPro = `SELECT * FROM property WHERE id = ${idNumber};`;
  let dbdataPro = await queryAsync(queryPro);

  let agentID = dbdataPro.agent_id;

  let queryAgent = `SELECT name, agents_photo FROM agents WHERE id = ${agentID};`;
  let dbdataAgent = await queryAsync(queryAgent);
  
  let rowsPro = JON.parse(JSON.stringify(dbdataPro));
  let rowsAgent = JON.parse(JSON.stringify(dbdataAgent));
  
  return {
    props:{
      rowsPro,
      rowsAgent
    },
  }
}

function Index({rowsPro, rowsAgent}){

  return (
    <>
    {rowsPro}
    <br />
    {rowsAgent}
    </>
  )
}

export default Index;
