import NavBar from './NavBar';
import MetaHead from './Head';
import Footer from "./Footer";
import Link from 'next/link';
import queryAsync from '../mysql';
// import AddProperty from './AddProperty';

import "../style.css"

export async function getServerSideProps(){

  let dbdata = await queryAsync(`
    SELECT property.id, property.address, property.image_path, property.inspection, 
    property.bed, property.shower, property.car, agents.name, agents.agents_photo 
    FROM property 
    JOIN agents ON property.agent_id = agents.id;
  `);
    
  let rows = JSON.JSON.stringify(dbdata);

  return {
    props: {
      rows
    },
  }
};


function Index({ rows }){

  let str ;
  for (let i = 0; i < rows.length; i++){
    str += `
    <div className = "imgs">
        <br>
        <p className = "right">Ultimo Real Estate</p>
        <br>
        <a target="_blank" href="./gallery?id=${rows[i].id}">
          <img src= '/static/${rows[i].image_path}' alt="housePicture" 
          style={{width: "100", height: 242;}}>
        </a>
        <br>
        <h5 className = "obi">${rows[i].name}
          <a href = "#">
            <img src = "/static/${rows[i].agents_photo}" width = "70px;" height="70px;" style = {{border-radius: "50";}}>
          </a>
        </h5>
        <br>
        <h4>${rows[i].address}</h4>
        <br>
        <p className = "icons">
          <img src = "/static/bed.png">${rows[i].bed}
          <img src = "/static/shower.png">${rows[i].shower}
          <img src = "/static/car.png">${rows[i].bed}</p>
        <p className = "inspe">
          <img src = "/static/planner.png"> Inspection : ${rows[i].inspection}</p>
        <br>
    </div>`
  };


  return (
    <>
    <MetaHead />
    <div className = "App">
      <header className = 'elseHeader'>
  
        <div>
          <Link href="/">
            <a>
              <img  src="../static/estate-logo.svg" width="300" height="100" 
              alt="Ultimo Real Estate, Sydney, Australia" />
            </a>
          </Link>
        </div>
  
        <NavBar />
      </header>
  
      <main>
        <div className= "titleOfPage">
          <h1>Property of the Week</h1>
        </div>
  
        <div className= "flex">
          <div className= "grid-container-pro">
            {str}
          </div>
        </div>
  
        
      </main>
    
      <Footer />
  
    </div>
  
    </>
  )
};


export default Index;
