import queryAsync from "../../mysql";

let x = 6;

async function getMoreAgents (req, res){

  let rows = await queryAsync(
    `SELECT * FROM agents LIMIT 3 OFFSET ${x}`
  );
  x = x + 3;

  
  let rowsMap = rows.map(row =>
    <div key = {row.id}>
      <img src={'static/' +row.agents_photo} alt="agen1" with="200" height="200" />
      <h4>{row.name}</h4>
      <p>{row.position}</p>
      <p><a href="#">{row.phone}</a></p>
      <p>{row.email}</p>
    </div>
  );


  res.status(200).json({
    rows
  });
}

export default getMoreAgents
