            />
          </div>
        </div>
        <div className="marginTop">
          <label >The number of Car Parking</label>
          <div id="radio">
            1
            <input 
              type="radio" 
              name="car" 
              value="1" 
              onChange = {handleChange} 
            />
            2
            <input 
              type="radio" 
              name="car" 
              value="2"
              onChange = {handleChange} 
            />
            3
            <input 
              type="radio" 
              name="car" 
              value="3"
              onChange = {handleChange}
            />
            4
            <input 
              type="radio" 
              name="car" 
              value="4" 
